mapper.update:
UPDATE base_admin_user


    SYS_USER_NAME = #{sysUserName},


    SYS_USER_PWD = #{sysUserPwd},


    ROLE_ID = #{roleId},


    USER_PHONE = #{userPhone}


WHERE id = #{id}

UPDATE base_admin_user
set SYS_USER_PWD = #{password}
where SYS_USER_NAME = #{userName}

UPDATE base_admin_user
SET USER_STATUS = #{status}
WHERE id = #{id}

mapper.select:
SELECT 
FROM base_admin_user
WHERE SYS_USER_NAME = #{sysUserName}
and USER_STATUS = 1

  and id != #{id}

SELECT id, SYS_USER_NAME AS sysUserName ,ROLE_ID AS roleId, (SELECT role_name FROM base_admin_role r WHERE r.id = u.ROLE_ID) as roleName, USER_PHONE AS userPhone,REG_TIME AS regTime,USER_STATUS AS userStatus FROM base_admin_user u
and SYS_USER_NAME LIKE '%' #{sysUserName} '%'
and USER_PHONE LIKE '%' #{userPhone} '%'
and REG_TIME > #{startTime}
and REG_TIME < #{endTime}
